import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import MovieList from './components/MovieList';
import Movie from './components/Movie';
import Marathon from './components/Marathon';
import MarathonList from './components/MarathonList'

class App extends Component {

  inputElement = React.createRef()

  constructor(props) {
    super(props);

    this.state = {
      marathons: [],
      currentMarathon: { marathon: '', key: ''}
    };
  }

  handleInput = (e) => {
    const marathon = e.target.value;
    const newMarathon = { currentMarathon: marathon, key: Date.now() }

    this.setState({
      currentMarathon: newMarathon
    })
  }

  addMarathon = (e) => {
    e.preventDefault();

    const marathonToAdd = this.state.currentMarathon
    const marathonList = [...this.state.marathons, marathonToAdd]

    this.setState({
      marathons: marathonList,
      currentMarathon: { marathon: '', key: '' }
    })
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <MarathonList
            currentMarathon={this.state.currentMarathon}
          />
        </header>
        <Marathon />
        <Marathon />
        <Marathon />
      </div>
    );
  }
}

export default App;
